import './App.css';
import React, { useState } from 'react';

const events = [
  {
    title: "Big meeting",
    desc: 'This is a big meeting',
    email: 'email@email.com',
    date: '2023-10-15',
    time: '12:00 AM',
  },
  {
    title: "Vacation",
    desc: 'This is a vacation',
    email: 'email@email.com',
    date: '2023-10-19',
    time: '10:00 AM',
  },
  {
    title: "Conference",
    desc: 'This is a conference',
    email: 'email@email.com',
    date: '2023-10-17',
    time: '09:00 AM',
  },
]






function App(){

  const [newEvent,setNewEvent] = useState({title:'', desc:'', email:'', date:'', time:''})
  const [allEvents,setAllEvents] = useState(events)

  function handleAddEvent(){
    setAllEvents([...allEvents,newEvent])
  }

  return(
    <div className='App'>
      <h1>Hadoop Project</h1>
      <div>
        <label for='eventTitle'> Event Title </label>
        <input
          type='text'
          placeholder='255 characters'
          id='eventTitle'
          maxLength={255}
          value={newEvent.title}
          onChange={(e) => setNewEvent({...newEvent,title: e.target.value})}
        />
        <label for='eventDesc'> Event Description </label>
        <input
          type='text'
          placeholder='600 characters'
          id='eventDesc'
          maxLength={600}
          value={newEvent.desc}
          onChange={(e) => setNewEvent({...newEvent,desc: e.target.value})}
        />
        <label for='hostEmail'> Host email </label>
        <input
          type='email'
          placeholder='Must be a valid email format'
          id='hostEmail'
          value={newEvent.email}
          onChange={(e) => setNewEvent({...newEvent,email: e.target.value})}
        />
        <div>
          <label for='date'> Event Date </label>
          <input
            type='text'
            pattern='\d{4}-\d{2}-\d{2}'
            placeholder='YYYY-MM-DD'
            value={newEvent.date}
            onChange={(e) => setNewEvent({...newEvent,date: e.target.value})}
          />
          <label for='time'> Event Time </label>
          <input
            type='text'
            pattern='(0[1-9]|1[0-2]):[0-5][0-9] (AM|PM)'
            placeholder='HH:MM AM/PM'
            value={newEvent.time}
            onChange={(e) => setNewEvent({...newEvent,time: e.target.value})}
            />
        </div>
        <button style={{marginTop: '10px'}}> Add Event </button>
        
      </div>
    </div>

  )
}

export default App
