import './App.css';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import "react-big-calendar/lib/css/react-big-calendar.css";
import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

import {TimePicker} from 'react-time-picker';


const locales = { 
  "en-US": require("date-fns/locale/en-US")
}

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales
});

function CustomEvent({ event }) {
  // Add the current user's email to the list of participants.
  const handleParticipants = () => {
    event.participants.push("The email");
  }

  return (
    <div className='custom-event'>
      <div>{event.title}</div>
      <div>Participants:</div>
      <button onClick={handleParticipants}>Join Event</button>
    </div>
  )
}

function App() {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({
    title: "",
    date: "",
    time: "12:00 PM",
    description: "",
    email: "",
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    const response = await fetch('/api/events/');
    const json = await response.json();

    if (response.ok) {
      const formattedEvents = json.map((eventData) => ({
        date: parse(eventData.date, 'yy-MM-dd', new Date()),
        time: eventData.time,
        description: eventData.description,
        email: eventData.email,
      }));

      console.log("Events fetched")
      setEvents(formattedEvents);
    }
  };


  const eventToAdd = {
    title: newEvent.title,
    date: format(newEvent.date, 'dd-MM-yy'),
    time: newEvent.time,
    description: newEvent.description,
    email: newEvent.email,
  };
  
  function handleAddEvent() {
    fetch('api/events/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventToAdd),
    })
      .then((response) => {
        if (response.ok) {
          // After successfully adding the event to the database, you can fetch the events again.
          fetchEvents();

          setNewEvent({
            date: '',
            time: '12:00 PM',
            title: '',
            description: '',
            email: '',
          });
        } else {
          throw new Error('Failed to add the event to the database.');
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  return (
    <div className='App'>
      <h1>Calendar</h1>
      <h2>Add New Event</h2>
      <div>
        <input
          type="text"
          placeholder='Add Title 255 characters'
          style={{ width: "20%", marginRight: "10px" }}
          value={newEvent.title}
          onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
          required
          maxLength={255}
        />
        <input
          type="email"
          placeholder='Email'
          style={{ width: "20%", marginRight: "10px" }}
          value={newEvent.email}
          onChange={(e) => setNewEvent({ ...newEvent, email: e.target.value })}
          required
          maxLength={255}
        />
        <input
          type="text"
          placeholder='Add description 600 characters'
          style={{ width: "15%", height: "75px", marginRight: "10px", justifyContent: "center", alignItems: "flex-start" }}
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
          required
          maxLength={600}
        />
        <div>
          <input
            type="text" 
            placeholder="Date" 
            style={{ marginRight: "10px" }}
            selected={newEvent.date} 
            onChange={(date) => setNewEvent({ ...newEvent, date })}
          />
        </div>
        <TimePicker
          type="text"
          placeholder="Time"
          style={{ marginRight: "10px" }}
          value={newEvent.time}
          onChange={(e) => setNewEvent({ ...newEvent, time: e.target.value })}
        />
        <button style={{ marginTop: "10px" }} onClick={handleAddEvent}>
          Add Event
        </button>
      </div>

          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: 500, margin: "50px" }}
            components={{ event: CustomEvent }}
          />
    </div>
  );
}

export default App;
